import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {


    // this.setState({ random: this.state.random + rand });

  render() {
    const food = ['cycla', 'spring', 'hoppa'];
    return (
      <div className="App">
        <RandomSection source={food}/>

      </div>
    );
  }
}

class RandomSection extends React.Component {
  render() {
    const source = this.props.source;
    const rand = Math.random() * source.length;
    const index = Math.floor(rand);
    return (
    <div> 
      <h2>Daily one</h2>
      <p>You should do this: {source[index]}</p>
      <LoadingButton/>
    </div>
  )
}
}

class LoadingButton extends React.Component {

  handleClick() {
    this.setState({ isLoading: true });
  }

  render() {
    return (
      <button onClick={this.handleClick} >Get Random Daily</button>
    );
  }
}
export default App;
